function u(n){const t=new Date(n),e=new Date().getTime()-t.getTime();if(e<2592e5){if(e<6e4)return"刚刚";if(e<36e5)return`${Math.floor(e/6e4)} 分钟前`;if(e<864e5)return`${Math.floor(e/36e5)} 小时前`;const r=Math.floor(e/864e5);if(r===1)return"昨天";if(r===2)return"前天"}return o(n)}function o(n){const t=new Date(n),a=t.getFullYear(),e=String(t.getMonth()+1).padStart(2,"0"),r=String(t.getDate()).padStart(2,"0");return`${a}-${e}-${r}`}function f(n){const t=new Date(n),a=t.getFullYear(),e=String(t.getMonth()+1).padStart(2,"0"),r=String(t.getDate()).padStart(2,"0"),i=String(t.getHours()).padStart(2,"0"),s=String(t.getMinutes()).padStart(2,"0"),c=String(t.getSeconds()).padStart(2,"0");return`${a}-${e}-${r} ${i}:${s}:${c}`}function g(){return{formatTimeAgo:u,formatDate:o,formatDateTime:f}}export{f as a,u as f,g as u};
